function createPokemonCardTemplate(t){const e=t.types.map(t=>createTypeButtonTemplate(t)).join("");return`\n    <div class="pokemon-card-header">\n      <h3 class="pokemon-name">${t.name}</h3>\n      <p class="pokemon-number">${t.id}</p>\n    </div>\n    <div class="pokemon-image-container">\n      <img class="pokemon-image" src="${t.sprites.other["official-artwork"].front_default||t.sprites.front_default}" alt="Official artwork of ${t.name}" loading="lazy">\n    </div>\n    <div class="pokemon-card-footer">${e}</div>\n  `}function detailTemplate(t,e,n,a){const o=t.types.map(t=>createTypeButtonTemplate(t)).join("");return`\n    ${createDetailsHeader(t)}\n    ${createImageSection(t)}\n    ${createTypeButtonContainer(o)}\n    ${createDetailOverlay(t,e,n,a)}\n  `}function createDetailsHeader(t){return`<div class="details-header">\n      <h2 style="display: inline-block; margin-right: 10px; text-transform: capitalize;">${t.name}</h2>\n      <span style="display: inline-block; font-size: 1.5rem; font-weight: bold;">${t.id}</span>\n    </div>`}function createImageSection(t){return`<div class="pokemon-image-section">\n      <img src="${t.sprites.other["official-artwork"].front_default}" alt="${t.name}" class="details-image" loading="lazy">\n    </div>`}function createTypeButtonContainer(t){return`<div class="type-button-container">${t}</div>`}function createDetailOverlay(t,e,n,a){return`\n    <div class="detail-overlay">\n      ${createTabContainer()}\n      ${createAboutTab(t,e,n,a)}\n      ${createBaseStatsTab(t)}\n      ${createMovesTab(t)}\n    </div>\n  `}function createTabContainer(){return'<div class="tab-container">\n      <button class="tab-button active" onclick="openTab(event, \'About\')">About</button>\n      <button class="tab-button" onclick="openTab(event, \'BaseStats\')">Base Stats</button>\n      <button class="tab-button" onclick="openTab(event, \'Moves\')">Moves</button>\n    </div>'}function createAboutTab(t,e,n,a){return`<div id="About" class="tab-content">\n      <div class="tab-table">\n        <table>\n          <tr><th>Species:</th><td>${t.species.name}</td></tr>\n          <tr><th>Height:</th><td>${e} m</td></tr>\n          <tr><th>Weight:</th><td>${n} kg</td></tr>\n          <tr><th>Abilities:</th><td>${a}</td></tr>\n        </table>\n      </div>\n    </div>`}function createBaseStatsTab(t){return`<div id="BaseStats" class="tab-content" style="display: none;">\n      <div class="tab-table">\n        <table>\n          ${t.stats.map(t=>`<tr><th>${capitalizeFirstLetter(t.stat.name)}:</th><td><progress value="${t.base_stat}" max="200"></progress>${t.base_stat}</td></tr>`).join("")}\n        </table>\n      </div>\n    </div>`}function createMovesTab(t){return`<div id="Moves" class="tab-content" style="display: none;">\n      <div class="moves-container" id="moves-${t.id}">Loading moves...</div>\n    </div>`}function errorMessageTemplate(t){return`<div class="error-message"><p>${t}</p><button onclick="init()">Wiederholen</button></div>`}const movesErrorTemplate=()=>"<p>Failed to load moves</p>";function createTypeButtonTemplate(t){return`<button class="type-button">\n      <img class="type-icon" src="${getTypeIconSrc(t.type.name)}" alt="${t.type.name}">\n      <span>${t.type.name}</span>\n    </button>`}function createMovesHTMLTemplate(t){return`<div class="moves-table-content">\n      ${t.map(t=>`<span class="move-compact-tag">${addHyphenation(t.name)}</span>`).join("")}\n    </div>`}